import React, { useState, useEffect } from 'react';
import '../style/Mainpage.scss';

const slides = [
  require('../img/image1.jpg'),
  require('../img/image2.jpg'),
  require('../img/image3.jpg'),
  require('../img/image4.jpg'),
  require('../img/image5.jpg')
];

const MainPage = () => {
  const [slideIndex, setSlideIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const [hovered, setHovered] = useState(false);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const disableScroll = (e) => e.preventDefault();
    window.addEventListener("wheel", disableScroll, { passive: false });

    return () => window.removeEventListener("wheel", disableScroll);
  }, []);

  useEffect(() => {
    if (isPaused) return;

    let time = 0;
    const progressInterval = setInterval(() => {
      time += 100;
      setProgress((time / 5000) * 100);
    }, 100);

    const slideTimer = setTimeout(() => {
      setSlideIndex((prevIndex) => (prevIndex + 1) % slides.length);
      setProgress(0);
    }, 5000);

    return () => {
      clearInterval(progressInterval);
      clearTimeout(slideTimer);
    };
  }, [slideIndex, isPaused]);

  const prevSlide = () => {
    setSlideIndex((prevIndex) => (prevIndex === 0 ? slides.length - 1 : prevIndex - 1));
    setProgress(0);
  };

  const nextSlide = () => {
    setSlideIndex((prevIndex) => (prevIndex + 1) % slides.length);
    setProgress(0);
  };

  const togglePause = () => {
    setIsPaused(!isPaused);
  };

  return (
    <div className="main-page">
      <div 
        className="logo-box" 
        onMouseEnter={() => setHovered(true)} 
        onMouseLeave={() => setHovered(false)}
      >
        {hovered ? "Jae-beom Park" : "JB"}
      </div>

      <div className="slide-show">
        <img 
          src={slides[slideIndex]} 
          alt={`슬라이드 ${slideIndex + 1}`} 
          key={slideIndex}
          className="fade-slide"
        />
      </div>

      {/* 삼성 슬라이드일 경우 추가 정보 박스 */}
      {slideIndex === 0 && (
        <div className="info-box">
          <p>삼성에 대해 알아보세요.</p>
          <a href="https://www.samsung.com" target="_blank" rel="noopener noreferrer">삼성 홈페이지</a>
        </div>
      )}

      <div className="progress-bar">
        <div className="progress" style={{ width: `${progress}%` }} />
      </div>

      <div className="controls">
        <button onClick={prevSlide}>〈</button>
        <button onClick={togglePause}>{isPaused ? '▶️' : '⏸️'}</button>
        <button onClick={nextSlide}>〉</button>
      </div>
    </div>
  );
};

export default MainPage;
