import React, { useState } from 'react';
import './Ham.scss';

const Ham = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="ham-menu">
      <div className={`burger ${isOpen ? 'open' : ''}`} onClick={toggleMenu}>
        <div className="line"></div>
        <div className="line"></div>
        <div className="line"></div>
      </div>
      {isOpen && (
        <div className="submenu">
          <ul>
            <li>로그인</li>
            <li>랭킹</li>
            <li>럭셔리</li>
            <li>남성</li>
            <li>여성</li>
          </ul>
        </div>
      )}
    </div>
  );
};

export default Ham;
