import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../style/Rank.scss';
import Menu from './Menu';
import shoeData from '../data/Rank-Shoe.json';
import CloData from '../data/Rank-Clo.json';  
import bagData from '../data/Rank-Bag.json'; 
import techData from '../data/Rank-Tech.json'; 
import legoData from '../data/Rank-Lego.json'; 

const Rank = () => {
  const [category, setCategory] = useState('Ïã†Î∞ú');
  const [rankingData, setRankingData] = useState([]);
  const [loading, setLoading] = useState(true);

  const dataMap = {
    Ïã†Î∞ú: shoeData,
    ÏùòÎ•ò: CloData,
    Í∞ÄÎ∞©: bagData,
    ÌÖåÌÅ¨: techData,
    Î†àÍ≥†: legoData,
  };

  useEffect(() => {
    if (category in dataMap) {
      setRankingData(dataMap[category]);
      setLoading(false);
    } else {
      setRankingData([]);
      setLoading(true);
    }
  }, [category]);

  const handleCategoryChange = (newCategory) => {
    setCategory(newCategory);
    setLoading(true);
  };

  return (
    <div className="rank-container">
      <Link to="/" className="home-button">Home</Link>
      <Menu />

      <h2>üèÜ Îû≠ÌÇπ ÌéòÏù¥ÏßÄ</h2>

      {/* Ïπ¥ÌÖåÍ≥†Î¶¨ Î≤ÑÌäº */}
      <div className="category-buttons">
        {['Ïã†Î∞ú', 'ÏùòÎ•ò', 'Í∞ÄÎ∞©', 'ÌÖåÌÅ¨', 'Î†àÍ≥†'].map((cat) => (
          <button
            key={cat}
            className={`category-button ${category === cat ? 'active' : ''}`}
            onClick={() => handleCategoryChange(cat)}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* Îç∞Ïù¥ÌÑ∞ ÌëúÏãú */}
      <ul className="ranking-list">
        {loading ? (
          <p>Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
        ) : (
          rankingData.length > 0 ? (
            rankingData.map((item, index) => (
              <li key={item.id} className="rank-item">
                <span>{index + 1}ÏúÑ - {item.name}</span><br />
                <img
                  src={require(`../img/${item.img}`).default}
                  alt={item.name}
                  className="rank-image"
                />
                <p>Í∞ÄÍ≤©: {item.price} Ïõê</p>
              </li>
            ))
          ) : (
            <p>Ìï¥Îãπ Ïπ¥ÌÖåÍ≥†Î¶¨ÏóêÎäî Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
          )
        )}
      </ul>
    </div>
  );
};

export default Rank;
