import React, { useState, useEffect } from 'react';
import '../style/Mainpage.scss';

const MainPage = () => {
  const [slideIndex, setSlideIndex] = useState(0);
  const [isHovered, setIsHovered] = useState(false);
  const [timer, setTimer] = useState(5);

  useEffect(() => {
    const slideTimer = setInterval(() => {
      setSlideIndex((prevIndex) => (prevIndex + 1) % 3);
    }, 3000);

    const countdownTimer = setInterval(() => {
      setTimer((prevTime) => (prevTime === 0 ? 5 : prevTime - 1));
    }, 1000);

    return () => {
      clearInterval(slideTimer);
      clearInterval(countdownTimer);
    };
  }, []);

  const handleHover = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  return (
    <div className="main-page">
      <div className="overlay" style={{ animationDuration: '5s' }}>
        <div className="name-box" onMouseEnter={handleHover} onMouseLeave={handleMouseLeave}>
          <div className="initials">{'J.B.'}</div>
          {isHovered && <div className="full-name">{'Jay Park'}</div>}
        </div>
      </div>
      <div className="timer-bar">
        <span>{`남은 시간: ${timer}s`}</span>
      </div>
      <div className="hamburger-menu">
        <button>☰</button>
      </div>
      <div className="slide-show">
        <div className={`slide slide-${slideIndex}`}>
          <img src={`image${slideIndex}.jpg`} alt={`슬라이드 ${slideIndex}`} />
          <div className="slide-text">슬라이드 {slideIndex + 1}</div>
        </div>
      </div>
      <div className="stop-button">
        <button>⏸️ 멈춤</button>
      </div>
    </div>
  );
};

export default MainPage;
