import React, { useState, useEffect } from 'react';
import '../style/Mainpage.scss';

const slides = [
  require('../img/image1.jpg'),
  require('../img/image2.jpg'),
  require('../img/image3.jpg'),
  require('../img/image4.jpg'),
  require('../img/image5.jpg')
];

const getSlideClass = (index) => {
  if (index === slideIndex) return "slide-active";
  if (index === (slideIndex - 1 + slides.length) % slides.length) return "slide-exit";
  return "slide-enter";
};

const MainPage = () => {
  const [slideIndex, setSlideIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false);

  useEffect(() => {
    const disableScroll = (e) => {
      e.preventDefault();
    };
    
    window.addEventListener("wheel", disableScroll, { passive: false });
  
    return () => {
      window.removeEventListener("wheel", disableScroll);
    };
  }, []);

  useEffect(() => {
    if (isPaused) return;
    
    const slideTimer = setInterval(() => {
      setSlideIndex((prevIndex) => (prevIndex + 1) % slides.length);
    }, 5000);

    return () => clearInterval(slideTimer);
  }, [isPaused]);

  const prevSlide = () => {
    setSlideIndex((prevIndex) => (prevIndex === 0 ? slides.length - 1 : prevIndex - 1));
  };

  const nextSlide = () => {
    setSlideIndex((prevIndex) => (prevIndex + 1) % slides.length);
  };

  const togglePause = () => {
    setIsPaused(!isPaused);
  };

  return (
    <div className="main-page">
      <div className="overlay">
        <div className="name-box">
          <div className="initials">{'J.B.'}</div>
        </div>
      </div>

      <div className="slide-show">
        {slides.map((src, index) => (
          <img key={index} src={src} alt={`슬라이드 ${index + 1}`} className={getSlideClass(index)} />
        ))}
      </div>

      <div className="controls">
        <button onClick={prevSlide}>〈</button>
        <button onClick={togglePause}>{isPaused ? '▶️' : '⏸️'}</button>
        <button onClick={nextSlide}>〉</button>
      </div>
    </div>
  );
};

export default MainPage;
