import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../style/Rank.scss';
import Menu from './Menu';
import shoeData from '../data/Rank-Shoe.json';

import shoe1 from '../img/shoe1.jpg';
import shoe2 from '../img/shoe2.jpg';
import shoe3 from '../img/shoe3.jpg';
import shoe4 from '../img/shoe4.jpg';
import shoe5 from '../img/shoe5.jpg';

const Rank = () => {
  const [category, setCategory] = useState('Ïã†Î∞ú');
  const [rankingData, setRankingData] = useState([]);

  useEffect(() => {
    setRankingData(shoeData);  // JSON Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©
  }, [category]);

  const handleCategoryChange = (newCategory) => {
    setCategory(newCategory);
  };

  // Ïù¥ÎØ∏ÏßÄ ÌååÏùºÏùÑ Í∞ùÏ≤¥Î°ú ÎßµÌïë
  const imageMap = {
    shoe1: shoe1,
    shoe2: shoe2,
    shoe3: shoe3,
    shoe4: shoe4,
    shoe5: shoe5
  };

  return (
    <div className="rank-container">
      <Link to="/" className="home-button">Home</Link>
      <Menu />

      <h2>üèÜ Îû≠ÌÇπ ÌéòÏù¥ÏßÄ</h2>
      <div className="category-buttons">
        {['Ïã†Î∞ú', 'ÏùòÎ•ò', 'Í∞ÄÎ∞©', 'ÌÖåÌÅ¨', 'Î†àÍ≥†'].map((cat) => (
          <button
            key={cat}
            className={`category-button ${category === cat ? 'active' : ''}`}
            onClick={() => handleCategoryChange(cat)}
          >
            {cat}
          </button>
        ))}
      </div>
      <ul>
        {rankingData.length > 0 ? (
          rankingData.map((item, index) => (
            <li key={index}>
              <div className="rank-item">
                <span>{index + 1}ÏúÑ - {item.name}</span><br />
                <img
                  src={imageMap[item.img]}
                  alt={item.name}
                  style={{ width: '100px', height: '100px' }}
                />
                <p>Í∞ÄÍ≤©: {item.price} Ïõê</p>
              </div>
            </li>
          ))
        ) : (
          <p>Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
        )}
      </ul>
    </div>
  );
};

export default Rank;
