import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../style/Rank.scss';
import Menu from './Menu';
import shoeData from '../data/Rank-Shoe.json';

const Rank = () => {
  const [category, setCategory] = useState('Ïã†Î∞ú');
  const [rankingData, setRankingData] = useState([]);
  const [loading, setLoading] = useState(true);  // Î°úÎî© ÏÉÅÌÉú Í¥ÄÎ¶¨

  useEffect(() => {
    // Ïπ¥ÌÖåÍ≥†Î¶¨ Í∞íÏù¥ 'Ïã†Î∞ú'Ïùº ÎïåÎßå Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò¥
    if (category === 'Ïã†Î∞ú') {
      setRankingData(shoeData); // 'Ïã†Î∞ú' Ïπ¥ÌÖåÍ≥†Î¶¨Ïùº Í≤ΩÏö∞ÏóêÎßå Îç∞Ïù¥ÌÑ∞ ÏÑ§Ï†ï
      setLoading(false);
    } else {
      setRankingData([]);  // Îã§Î•∏ Ïπ¥ÌÖåÍ≥†Î¶¨Ïùº ÎïåÎäî Îπà Îç∞Ïù¥ÌÑ∞Î°ú ÏÑ§Ï†ï
      setLoading(true);  // Î°úÎî© ÏÉÅÌÉúÎ°ú ÌëúÏãú
    }
  }, [category]);

  const handleCategoryChange = (newCategory) => {
    setCategory(newCategory);
  };

  return (
    <div className="rank-container">
      <Link to="/" className="home-button">Home</Link>
      <Menu />

      <h2>üèÜ Îû≠ÌÇπ ÌéòÏù¥ÏßÄ</h2>
      <div className="category-buttons">
        {['Ïã†Î∞ú', 'ÏùòÎ•ò', 'Í∞ÄÎ∞©', 'ÌÖåÌÅ¨', 'Î†àÍ≥†'].map((cat) => (
          <button
            key={cat}
            className={`category-button ${category === cat ? 'active' : ''}`}
            onClick={() => handleCategoryChange(cat)}
          >
            {cat}
          </button>
        ))}
      </div>
      <ul>
        {loading ? (
          <p>Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p> // Î°úÎî© Ï§ëÏùº Îïå Î©îÏãúÏßÄ ÌëúÏãú
        ) : (
          rankingData.length > 0 ? (
            rankingData.map((item, index) => (
              <li key={index}>
                <div className="rank-item">
                  <span>{index + 1}ÏúÑ - {item.name}</span><br />
                  <img
                    src={require(`../img/${item.img}`).default}
                    alt={item.name}
                    style={{ width: '100px', height: '100px' }}
                />
                  <p>Í∞ÄÍ≤©: {item.price} Ïõê</p>
                </div>
              </li>
            ))
          ) : (
            <p>Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
          )
        )}
      </ul>
    </div>
  );
};

export default Rank;
