import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../style/Rank.scss';
import Menu from './Menu';
import luxuryData from '../data/Rank-Luxury.json'; // Îç∞Ïù¥ÌÑ∞ ÌååÏùº Î∂àÎü¨Ïò§Í∏∞

const Luxury = () => {
  const [category, setCategory] = useState('Îü≠ÏÖîÎ¶¨');
  const [rankingData, setRankingData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞
    if (category === 'Îü≠ÏÖîÎ¶¨') {
      setRankingData(luxuryData);
      setLoading(false);
    }
  }, [category]);

  const handleCategoryChange = (newCategory) => {
    setCategory(newCategory);
    setLoading(true);
  };

  // ÏàúÏúÑ Íµ¨Í∞Ñ ÎÇòÎàÑÍ∏∞
  const getRankGroup = (index) => {
    if (index < 5) return '1-5Îì±';
    if (index < 10) return '6-10Îì±';
    if (index < 15) return '11-15Îì±';
    return '16-20Îì±';
  };

  return (
    <div className="rank-container">
      <Link to="/" className="home-button">Home</Link>
      <Menu />

      <h2>üèÜ Îü≠ÏÖîÎ¶¨ Îû≠ÌÇπ</h2>

      <div className="category-buttons">
        <button
          className={`category-button ${category === 'Îü≠ÏÖîÎ¶¨' ? 'active' : ''}`}
          onClick={() => handleCategoryChange('Îü≠ÏÖîÎ¶¨')}
        >
          Îü≠ÏÖîÎ¶¨
        </button>
      </div>

      <ul className="ranking-list">
        {loading ? (
          <p>Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
        ) : (
          rankingData.length > 0 ? (
            ['1-5Îì±', '6-10Îì±', '11-15Îì±', '16-20Îì±'].map((group) => (
              <div key={group}>
                <h3>{group}</h3>
                <ul>
                  {rankingData
                    .filter((_, index) => getRankGroup(index) === group)
                    .map((item, index) => (
                      <li key={item.id} className="rank-item">
                        <span>{index + 1}ÏúÑ - {item.name}</span><br />
                        <img src={item.img} alt={item.name} className="rank-image" />
                        <p>Í∞ÄÍ≤©: {item.price.toLocaleString()} Ïõê</p>
                      </li>
                    ))}
                </ul>
              </div>
            ))
          ) : (
            <p>Ìï¥Îãπ Ïπ¥ÌÖåÍ≥†Î¶¨ÏóêÎäî Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
          )
        )}
      </ul>
    </div>
  );
};

export default Luxury;
